'use strict'
var recast = require('recast')
var esprima = require('esprima-fb')
var escodegen = require('escodegen')
//var code = "import _ from 'a'"

const code = `import React from 'react/addons';
import _ from 'lodash';


export default function(){ return React.createElement('div',{})}`


function recast1() {
    var tree = recast.parse(code)
    var s = recast.print(tree).code //escodegen.generate(tree, {comment: true})
    console.log(s)
}

function escodegenTest() {
    //var tree = esprima.parse(code)
    //var tree = esprima.parse(code, {sourceType: 'module'})
    var tree = esprima.parse(code, {attachComment: true, loc: false, range: false, sourceType: 'module', tokens: true})
    var s = escodegen.generate(tree)
    //var s = escodegen.generate(tree, {comment: true})
    console.log(s)
}

//recast1()
escodegenTest()


